import Foundation

class QuizNewYear: QuizBaseViewModel {
    
    override func questions() -> [Question] {
        return [Question(question: "Как величали сурового предшественника современного русского Деда Мороза?", choices: ["Дед Колотун","Дед Трескун","Дед Вьюговей"], answer: "Дед Трескун", image: "newyear1.jpeg", levelOfdifficulty: .easy), Question(question: "По указу какого царя датой празднования Нового года на Руси стало 1 января", choices: ["Ивана Грозного","Александра I","Петра I"], answer: "Петра I", image: "newyear2.jpeg", levelOfdifficulty: .easy), Question(question: "В какой стране в XVI веке появилась первая елочная игрушка?", choices: ["Саксония","Австралия","Германия"], answer: "Саксония", image: "newyear3.jpeg", levelOfdifficulty: .normal), Question(question: "В какой стране с 19 по 21 ноября проходит всемирная Олимпиада Санта Клаусов и Дедов Морозов?", choices: ["Нидерланды","Швеция","Франция"], answer: "Швеция", image: "newyear4.jpeg", levelOfdifficulty: .hard), Question(question: "Какие из перечисленных соревнований включены в программу Олимпийских игр Санта Клаусов и Дедов Морозов?", choices: ["Гонки на оленьих упряжках","соревнования в скорости украшения елок","соревнования в мастерстве передвижения на снегоступах"], answer: "соревнования в мастерстве передвижения на снегоступах", image: "newyear5.jpeg", levelOfdifficulty: .easy), Question(question: "У представителей какого народа под Новый год принято вспоминать о совершенных грехах и давать обещание искупить их новыми делами в Новом году?", choices: ["евреи","греки","японцы"], answer: "евреи", image: "newyear6.jpeg", levelOfdifficulty: .normal), Question(question: "Китайцы считают, что первый день наступившего года окутан злыми духами, которых необходимо отпугнуть. Чем китайцы их отпугивают?", choices: ["чаем","петардами","рисом"], answer: "петардами", image: "newyear7.jpeg", levelOfdifficulty: .hard), Question(question: "Деда Мороза какой страны зовут Йоулупукки, или Лесной Человек?", choices: ["Финляндия","Италия","Нидерланды"], answer: "Финляндия", image: "newyear8.jpeg", levelOfdifficulty: .easy), Question(question: " В каком городе получил прописку российский Дед Мороз?", choices: ["Новгород","Тула","Великий Устюг"], answer: "Великий Устюг", image: "newyear9.jpeg", levelOfdifficulty: .normal), Question(question: "Где находится резиденция белорусского Деда Мороза?", choices: ["Березинский заповедник","Беловежская пуща","Мирский замок"], answer: "Беловежская пуща", image: "newyear10.jpeg", levelOfdifficulty: .hard), Question(question: "Жители какой страны во время наступления Нового года, веря в то, что смех обязательно принесет в наступающем году удачу, начинают смеяться?", choices: ["Япония","Китай","Италия"], answer: "Япония", image: "newyear11.jpeg", levelOfdifficulty: .easy), Question(question: "Жители какой страны верят в то, что бог плавает на спине карпа? Поэтому по традиции большинство из них в канун Нового года выпускают в реку живого карпа.", choices: ["Вьетнам","Австрия","Китай"], answer: "Вьетнам", image: "newyear12.jpeg", levelOfdifficulty: .normal), Question(question: "Жители каких стран перед наступлением Нового года распахивают дверь, чтобы отпустить Старый год и впустить Новый?", choices: ["Шотландия, Уэльс","Испания","Финляндия"], answer: "Шотландия, Уэльс", image: "newyear13.jpeg", levelOfdifficulty: .hard), Question(question: " Жители какой страны перед наступление Нового года катят по улицам города бочку с горящей смолой? Таким образом они сжигают все невзгоды, которые были в Старом году.", choices: ["Шотландия","Дания","Франция"], answer: "Шотландия", image: "newyear14.jpeg", levelOfdifficulty: .easy), Question(question: "Жители какой страны в первые часы Нового года из окон своих квартир и домов выбрасывают весь ненужный хлам?", choices: ["Италия","Нидерланды","Испания"], answer: "Италия", image: "newyear15.jpeg", levelOfdifficulty: .normal), Question(question: "Страна первой украшенной елочки", choices: ["Германия","Бразилия","Турция"], answer: "Германия", image: "newyear16.jpeg", levelOfdifficulty: .hard), Question(question: "Этот предмет собирает больше всего людей на любом празднике", choices: ["стол","камин","телевизор"], answer: "Стол", image: "newyear17.jpeg", levelOfdifficulty: .easy), Question(question: "Кто жил в ледяной избушке, у леса на опушке?", choices: ["Зима","Осень","Весна"], answer: "Зима", image: "newyear18.jpeg", levelOfdifficulty: .normal), Question(question: "Зимнее явление, чье составляющее никогда не бывает одинаковым?", choices: ["снежинки","град","дождь"], answer: "снежинки", image: "newyear19.jpeg", levelOfdifficulty: .hard),Question(question: "Главный символ Нового года.", choices: ["елка","сосна","береза"], answer: "елка", image: "newyear20.jpeg", levelOfdifficulty: .easy)]
        
    }
}
